import React from 'react'
import { Link } from 'react-router-dom'
import './FilledBtn.sass'

export default function FilledBtn(props) {
  return (
    <a href={props.to} onClick={e => props.handler(e, props.forwardRef)} className="filledBtn" style={{background: "linear-gradient(275.96deg, #9373DE 0%, #AB87FF 100%)"}}>
      <p className="filledBtn__text">{props.text}</p>
    </a>
  )
}
