import React from 'react'
import './PageFirstScreen.sass'
import FilledBtn from 'component/pageComponents/MainPage/FilledBtn'
import SocialLinksBar from 'component/pageComponents/MainPage/SocialLinksBar'
import bgImg from "assets/img/LogoPng.png";

const style = {
  backgroundColor: '#F0F2F7'
}

export default function PageFirstScreen(props) {

  const scrollToRef = (e, ref) => {
    e.preventDefault()
    window.scrollTo({top: ref.current.offsetTop, behavior: 'smooth'})
  }

  return (
    <header className="firstScreen" style={style} ref={props.refs.topRef}>
      <div className="container">

        <div className="firstScreen__wrapper">
          <div className="firstScreen__wrapper__info">
            <article className="firstScreen__heading" >
              <h2 className="firstScreen__title">Earn rewards on DEFI.<br />Secure and non-custodial staking</h2>
              <img src={bgImg} alt="logo" className={`image`}/>
              <h3 className="firstScreen__subtitle">We help investors to profit off their crypto assets constantly increasing yield</h3>
              <div className="firstScreen__btn">
                <FilledBtn forwardRef={props.refs.validatorsRef} handler={scrollToRef} text="Stake now" />
              </div>
            </article>

          </div>

          <div className="firstScreen__social">
            <SocialLinksBar />
          </div>
          <div className="firstScreen__socialBar">
            <SocialLinksBar horizontal transparent/>
          </div>
        </div>
      </div>
    </header>
  )
}