import React from 'react'
import './StakingBrandCard.sass'
import { connect } from 'react-redux'
import { showPopUp, setPopupInfo } from 'store/MainPage/actions'

function StakingBrandCard({
    icon,
    title,
    abbr,
    apy,
    fee,
    id,
    showPopUp,
    setPopupInfo,
    explorerLink,
    discordLink
  }) {
    
  return (
    <div className="stakingCard">
      <h5 className="stakingCard__title">{title}</h5>

      <div className="stakingCard__img-container">
        <img src={icon} alt={title} className="stakingCard__icon" />
      </div>

      <div className="stakingCard__content">
        {apy ? 
          <p className="stakingCard__apy">APY <span>+{apy}%</span></p>
        : 
          <p className="stakingCard__text"><br /></p>
        }
        <button
         className="stakingCard__btn"
         onClick={() => {
           showPopUp(true);
           setPopupInfo({icon, title, abbr, apy, fee, id, explorerLink, discordLink})}}
        >Staking details</button>
      </div>
    </div>
  )
}

const mapDispatchToProps = {
  showPopUp,
  setPopupInfo
}

export default connect(null, mapDispatchToProps)(StakingBrandCard)