import React, { useState, useEffect } from 'react'
import './Examples.sass'
import background from 'assets/img/partnersExamplesBG.png'
import backgroundMobile from 'assets/img/partnersExamplesBGMobile.jpg'

export default function Examples() {

  const [isMobile, setIsMobile] = useState(window.innerWidth < 1100)

  useEffect(() => {
    function onWindowResize () {
      return window.innerWidth < 1100 ? setIsMobile(true) : setIsMobile(false)
    }
    window.addEventListener('resize', onWindowResize);
    return () => {
      window.removeEventListener('resize', onWindowResize);
    }
  }, [])

  return (
    <div className="partnersExamples" style={{backgroundImage: `url(${isMobile ? backgroundMobile : background})`}}>
      <div className="container partnerscontainer">
        <h4 className="partnersExamples__title">Some examples of our work</h4>
        <ul className="partnersExamples__list">
          <li><a href="https://mina.staketab.com/" target="_blank" className="partnersExamples__item">Mina validators board and block explorer</a></li>
          <li><a href="https://mina.staketab.com/validators/rewards" target="_blank" className="partnersExamples__item">Validators analytics tool for rewarding estimation and stats</a></li>
          <li><a href="https://github.com/Staketab" target="_blank" className="partnersExamples__item">Scripts</a></li>
          <li><a href="https://docs.staketab.com/" target="_blank" className="partnersExamples__item">Staking and validation documentation</a></li>
        </ul>
      </div>
    </div>
  )
}
