import React, { useState, useEffect } from 'react'
import './FirstScreen.sass'
import logoImg from 'assets/img/staketabLogo.svg'
import background from 'assets/img/partnersBG.jpg'
import backgroundMobile from 'assets/img/PartnersBGMobile.jpg'
import { Link } from 'react-router-dom'

export default function FirstScreen() {

  const [isMobile, setIsMobile] = useState(window.innerWidth < 1100)

  useEffect(() => {
    function onWindowResize () {
      return window.innerWidth < 1100 ? setIsMobile(true) : setIsMobile(false)
    }
    window.addEventListener('resize', onWindowResize);
    return () => {
      window.removeEventListener('resize', onWindowResize);
    }
  }, [])

  return (
    <header className="partnersFirstScreen" style={{backgroundImage: `url(${isMobile ? backgroundMobile : background})`}}>
      <div className="container partnerscontainer">

        <nav className="partnersFirstScreen__nav">
          <Link to="/"><img src={logoImg} alt="Logo" /></Link>
        </nav>

        <section className="partnersFirstScreen__heading">
          <p className="partnersFirstScreen__subtitle">Staking Provider You Can Trust</p>
          <h3 className="partnersFirstScreen__title">Take your blockchain project to new heights</h3>
          <p className="partnersFirstScreen__text">
            Here at Staketab, we are interested in a mutually beneficial cooperation with cryptoproject teams. 
          </p>

        </section>

      </div>
    </header>
  )
}
